$(document).ready(function(){var t=0,n=null;$(".btn_modal_order_now").click(function(){n=$(this).data("product"),t=n.price,document.getElementById("modal_image").src=n.image.url,$("#modal_name").html(n.name),$("#modal_price").html(t.toLocaleString()+I18n.t("cart.vnd"));$("#modal_quantity").html("1");$("#modal_total").html(t.toLocaleString()+I18n.t("cart.vnd"))}),$(".modal_quantity_up").click(function(){var n=parseInt($("#modal_quantity").html());n<30&&($("#modal_quantity").html(n+1),$("#modal_total").html(((n+1)*t).toLocaleString()+I18n.t("cart.vnd")))}),$(".modal_quantity_down").click(function(){var n=parseInt($("#modal_quantity").html());n>1&&($("#modal_quantity").html(n-1),$("#modal_total").html(((n-1)*t).toLocaleString()+I18n.t("cart.vnd")))}),$("#finish_order_fast").click(function(){var t=$("#modal_quantity").html(),o=$("#modal_note").val();$.ajax({type:"POST",url:"/order_fasts",dataType:"json",context:this,data:{product_id:n.id,note:o,quantity:t},success:function(t){""==t.url?window.location.reload():window.location=t.url},error:function(){window.location.reload()}})})}),$(document).ready(function(){$(".update_notes_cart").click(function(){$(this).closest(".hide_show").find(".content_note_item_cart").slideToggle(800)}),$(".content_note_cancel").click(function(){$(this).closest(".hide_show").find(".content_note_item_cart").slideToggle(800)}),$(".content_note_save").click(function(){var t=$(this).closest(".content_note_item_cart").find(".content_note").val(),n=($(this).closest(".cart-item-show").data("id"),$(this).closest("a").data("id"));$.ajax({type:"PUT",url:"/carts/1",dataType:"json",context:this,data:{type_notes:"notes",product_id:n,notes:t},success:function(){$(this).closest(".content_note_item_cart").hide(800)},error:function(){alert(I18n.t("order_quick.save_fail"))}})})}),$(document).ready(function(){$("#search").keyup(function(){$("#search_owner_form").submit()})});